import React from "react";
import ReactWow from "react-wow";

const TeamMember2 = ({
  name,
  role,
  image,
  delay,
  removePadding,
  titulo,
  subtitle,
  body,
}) => {
  const handleClick = () => {
    const message = `Quiero informaci칩n sobre el servicio "${titulo}"`;
    const whatsappLink = `https://api.whatsapp.com/send?phone=123456789&text=${encodeURIComponent(
      message
    )}`;
    window.open(whatsappLink);
  };

  return (
    <ReactWow
      animation="fadeInUp"
      delay={`0.${delay}s`}>
      <div className="service-card">
        <div className="service-card-container">
          <div className="service-card-img">
            <img
              src={require("../../assets/images/" + image)}
              className="img-responsive"
              alt={image}
            />
          </div>
          <div className="card-caption">
            <div className="card-description">
              <div className="card-description-wrap">
                <h4 className="card-title">{titulo}</h4>
                <p className="card-subtitle">{subtitle}</p>
                <div className="card-service-body">
                  {body.length <= 5 ? (
                    body.map((paragraph, index) => (
                      <p key={index}>{paragraph}</p>
                    ))
                  ) : (
                    <div className="row">
                      <div className="col-sm-6">
                        {body.slice(0, 5).map((paragraph, index) => (
                          <p key={index}>{paragraph}</p>
                        ))}
                      </div>
                      <div className="col-sm-6">
                        {body.slice(5).map((paragraph, index) => (
                          <p key={index}>{paragraph}</p>
                        ))}
                      </div>
                    </div>
                  )}
                </div>
                <button
                  className="btn btn-primary"
                  onClick={handleClick}>
                  M치s informaci칩n
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
    </ReactWow>
  );
};

export default TeamMember2;
